import { DnD5eCharacter } from "@/types/character";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { getAllClasses } from "@/data/classes";
import { Check, Heart, Shield, Sparkles } from "lucide-react";

interface ClassSelectionStepProps {
  character: Partial<DnD5eCharacter>;
  setCharacter: (character: Partial<DnD5eCharacter>) => void;
}

export const ClassSelectionStep = ({ character, setCharacter }: ClassSelectionStepProps) => {
  const classes = getAllClasses();

  const handleClassSelect = (cls: any) => {
    // Auto-apply saving throw proficiencies from class
    const savingThrows: any = {};
    const abilityMap: Record<string, string> = {
      "Strength": "strength",
      "Dexterity": "dexterity",
      "Constitution": "constitution",
      "Intelligence": "intelligence",
      "Wisdom": "wisdom",
      "Charisma": "charisma"
    };

    cls.savingThrows.forEach((save: string) => {
      const abilityKey = abilityMap[save];
      if (abilityKey) {
        savingThrows[abilityKey] = true;
      }
    });

    setCharacter({ 
      ...character, 
      class: cls.name,
      savingThrows: savingThrows,
      skills: character.skills || {} // Preserve existing skills
    });
  };

  return (
    <div className="space-y-6">
      <Card className="border-2">
        <CardHeader>
          <CardTitle>Choose Your Class</CardTitle>
          <CardDescription>
            Your class defines your character's abilities, skills, and role in the party
          </CardDescription>
        </CardHeader>
      </Card>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        {classes.map((cls) => {
          const isSelected = character.class === cls.name;

          return (
            <Card
              key={cls.id}
              className={`cursor-pointer transition-all hover:scale-[1.02] hover:shadow-lg ${
                isSelected
                  ? "border-primary border-2 bg-primary/5"
                  : "border-border hover:border-primary/50"
              }`}
              onClick={() => handleClassSelect(cls)}
            >
              <CardHeader>
                <div className="flex items-start justify-between">
                  <div className="flex-1">
                    <CardTitle className="text-xl">{cls.name}</CardTitle>
                    <div className="mt-2 space-y-1 text-sm text-muted-foreground">
                      <div className="flex items-center gap-2">
                        <Heart className="h-3 w-3" />
                        <span>Hit Die: d{cls.hitDie}</span>
                      </div>
                      {cls.spellcasting && (
                        <div className="flex items-center gap-2">
                          <Sparkles className="h-3 w-3" />
                          <span>Spellcaster ({cls.spellcasting.ability})</span>
                        </div>
                      )}
                    </div>
                  </div>
                  {isSelected && (
                    <div className="p-1 rounded-full bg-primary">
                      <Check className="h-4 w-4 text-primary-foreground" />
                    </div>
                  )}
                </div>
              </CardHeader>
              <CardContent className="space-y-3">
                <div>
                  <p className="text-sm font-medium mb-2">Primary Abilities:</p>
                  <div className="flex flex-wrap gap-2">
                    {cls.primaryAbility.map((ability, idx) => (
                      <Badge key={idx} variant="secondary" className="text-xs capitalize">
                        {ability}
                      </Badge>
                    ))}
                  </div>
                </div>

                <div>
                  <p className="text-sm font-medium mb-2">Proficiencies:</p>
                  <div className="space-y-1 text-xs text-muted-foreground">
                    {cls.armorProficiencies.length > 0 && (
                      <div className="flex items-start gap-2">
                        <Shield className="h-3 w-3 mt-0.5 flex-shrink-0" />
                        <span>{cls.armorProficiencies.join(", ")}</span>
                      </div>
                    )}
                    <div className="flex items-start gap-2">
                      <span className="text-xs">тЪФя╕П</span>
                      <span>{cls.weaponProficiencies.join(", ")}</span>
                    </div>
                  </div>
                </div>

                {cls.subclasses && cls.subclasses.length > 0 && (
                  <div>
                    <p className="text-xs text-muted-foreground">
                      Subclasses: {cls.subclasses.slice(0, 2).join(", ")}
                      {cls.subclasses.length > 2 && ` +${cls.subclasses.length - 2} more`}
                    </p>
                  </div>
                )}
              </CardContent>
            </Card>
          );
        })}
      </div>

      {character.class && (
        <Card className="border-primary/50 bg-primary/5">
          <CardContent className="pt-6">
            <p className="text-sm text-muted-foreground">
              Selected: <span className="font-semibold text-foreground">{character.class}</span>
            </p>
          </CardContent>
        </Card>
      )}
    </div>
  );
};

import { Combatant, ConditionType } from "@/types/combat";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Progress } from "@/components/ui/progress";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Label } from "@/components/ui/label";
import { Shield, Heart, Skull, Trash2, User, UserPlus, Plus, X, AlertCircle } from "lucide-react";
import { useState } from "react";

interface CombatParticipantProps {
  combatant: Combatant;
  isCurrentTurn: boolean;
  onUpdateHP: (id: string, newHP: number) => void;
  onRemove: (id: string) => void;
  onUpdateConditions: (id: string, conditions: any[]) => void;
}

export const CombatParticipant = ({
  combatant,
  isCurrentTurn,
  onUpdateHP,
  onRemove,
  onUpdateConditions,
}: CombatParticipantProps) => {
  const [damageInput, setDamageInput] = useState("");
  const [showConditionDialog, setShowConditionDialog] = useState(false);
  const [newCondition, setNewCondition] = useState<ConditionType>("poisoned");
  const [conditionDuration, setConditionDuration] = useState("1");
  const [conditionNotes, setConditionNotes] = useState("");

  const hpPercentage = (combatant.hitPoints.current / combatant.hitPoints.max) * 100;

  const getHPColor = () => {
    if (hpPercentage > 50) return "text-green-600 dark:text-green-400";
    if (hpPercentage > 25) return "text-amber-600 dark:text-amber-400";
    return "text-destructive";
  };

  const getHPProgressColor = () => {
    if (hpPercentage > 50) return "bg-green-500";
    if (hpPercentage > 25) return "bg-amber-500";
    return "bg-destructive";
  };

  const applyDamage = () => {
    const amount = parseInt(damageInput) || 0;
    if (amount > 0) {
      const newHP = Math.max(0, combatant.hitPoints.current - amount);
      onUpdateHP(combatant.id, newHP);
      setDamageInput("");
    }
  };

  const applyHealing = () => {
    const amount = parseInt(damageInput) || 0;
    if (amount > 0) {
      const newHP = Math.min(combatant.hitPoints.max, combatant.hitPoints.current + amount);
      onUpdateHP(combatant.id, newHP);
      setDamageInput("");
    }
  };

  const quickAdjust = (amount: number) => {
    const newHP = Math.max(0, Math.min(combatant.hitPoints.max, combatant.hitPoints.current + amount));
    onUpdateHP(combatant.id, newHP);
  };

  const addCondition = () => {
    const duration = parseInt(conditionDuration);
    if (isNaN(duration) || duration < -1) return;

    const condition = {
      id: crypto.randomUUID(),
      type: newCondition,
      duration,
      notes: conditionNotes || undefined,
    };

    const updatedConditions = [...(combatant.conditions || []), condition];
    onUpdateConditions(combatant.id, updatedConditions);

    setNewCondition("poisoned");
    setConditionDuration("1");
    setConditionNotes("");
    setShowConditionDialog(false);
  };

  const removeCondition = (conditionId: string) => {
    const updatedConditions = (combatant.conditions || []).filter((c) => c.id !== conditionId);
    onUpdateConditions(combatant.id, updatedConditions);
  };

  const typeConfig = {
    player: { icon: User, color: "bg-blue-500/10 text-blue-700 dark:text-blue-400 border-blue-500/20" },
    ally: { icon: UserPlus, color: "bg-green-500/10 text-green-700 dark:text-green-400 border-green-500/20" },
    enemy: { icon: Skull, color: "bg-red-500/10 text-red-700 dark:text-red-400 border-red-500/20" },
  };

  const TypeIcon = typeConfig[combatant.type].icon;
  const isDead = combatant.hitPoints.current === 0;

  return (
    <Card
      className={`transition-all ${
        isCurrentTurn ? "border-primary border-2 shadow-lg shadow-primary/20" : ""
      } ${isDead ? "opacity-60" : ""}`}
    >
      <CardContent className="p-4 space-y-3">
        <div className="flex items-start justify-between">
          <div className="flex-1 min-w-0">
            <div className="flex items-center gap-2 mb-1">
              <Badge variant="outline" className={typeConfig[combatant.type].color}>
                <TypeIcon className="w-3 h-3 mr-1" />
                {combatant.type}
              </Badge>
              {isCurrentTurn && (
                <Badge className="animate-pulse">Active Turn</Badge>
              )}
            </div>
            <h3 className="font-semibold text-lg truncate">{combatant.name}</h3>
            <div className="flex gap-3 mt-1 text-sm text-muted-foreground">
              <div className="flex items-center gap-1">
                <Shield className="w-3 h-3" />
                <span>AC {combatant.armorClass}</span>
              </div>
              <div className="flex items-center gap-1">
                <span className="font-bold text-primary">#{combatant.initiative}</span>
                {combatant.initiativeBonus !== undefined && combatant.initiativeBonus !== 0 && (
                  <span className="text-xs">({combatant.initiativeBonus >= 0 ? '+' : ''}{combatant.initiativeBonus})</span>
                )}
                <span>Init</span>
              </div>
            </div>
          </div>
          <Button
            variant="ghost"
            size="icon"
            className="h-8 w-8 text-destructive hover:text-destructive"
            onClick={() => onRemove(combatant.id)}
          >
            <Trash2 className="w-4 h-4" />
          </Button>
        </div>

        {combatant.conditions && combatant.conditions.length > 0 && (
          <div className="flex flex-wrap gap-1">
            {combatant.conditions.map((condition) => (
              <Badge
                key={condition.id}
                variant="secondary"
                className="text-xs gap-1 pr-1 bg-amber-500/10 text-amber-700 dark:text-amber-400 border-amber-500/20"
              >
                <AlertCircle className="w-3 h-3" />
                {condition.type}
                {condition.duration > 0 && ` (${condition.duration})`}
                {condition.duration === -1 && " (тИЮ)"}
                <Button
                  variant="ghost"
                  size="icon"
                  className="h-4 w-4 p-0 hover:bg-transparent"
                  onClick={() => removeCondition(condition.id)}
                >
                  <X className="w-3 h-3" />
                </Button>
              </Badge>
            ))}
          </div>
        )}

        <div className="space-y-2">
          <div className="flex items-center justify-between text-sm">
            <div className="flex items-center gap-1">
              <Heart className="w-4 h-4" />
              <span className="font-medium">Hit Points</span>
            </div>
            <span className={`font-bold ${getHPColor()}`}>
              {combatant.hitPoints.current} / {combatant.hitPoints.max}
            </span>
          </div>
          <Progress value={hpPercentage} className="h-2">
            <div className={`h-full ${getHPProgressColor()} transition-all`} />
          </Progress>
        </div>

        <div className="grid grid-cols-4 gap-1">
          <Button variant="outline" size="sm" onClick={() => quickAdjust(-5)}>
            -5
          </Button>
          <Button variant="outline" size="sm" onClick={() => quickAdjust(-1)}>
            -1
          </Button>
          <Button variant="outline" size="sm" onClick={() => quickAdjust(1)}>
            +1
          </Button>
          <Button variant="outline" size="sm" onClick={() => quickAdjust(5)}>
            +5
          </Button>
        </div>

        <div className="flex gap-2">
          <Input
            type="number"
            placeholder="Amount"
            value={damageInput}
            onChange={(e) => setDamageInput(e.target.value)}
            onKeyDown={(e) => {
              if (e.key === "Enter") applyDamage();
            }}
            className="flex-1"
          />
          <Button variant="destructive" size="sm" onClick={applyDamage}>
            Damage
          </Button>
          <Button variant="default" size="sm" onClick={applyHealing}>
            Heal
          </Button>
        </div>

        <Dialog open={showConditionDialog} onOpenChange={setShowConditionDialog}>
          <DialogTrigger asChild>
            <Button variant="outline" size="sm" className="w-full">
              <Plus className="w-4 h-4 mr-1" />
              Add Condition
            </Button>
          </DialogTrigger>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>Add Condition</DialogTitle>
              <DialogDescription>Apply a status effect to this combatant</DialogDescription>
            </DialogHeader>
            <div className="space-y-4">
              <div className="space-y-2">
                <Label>Condition Type</Label>
                <Select value={newCondition} onValueChange={(v: ConditionType) => setNewCondition(v)}>
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="blinded">Blinded</SelectItem>
                    <SelectItem value="charmed">Charmed</SelectItem>
                    <SelectItem value="deafened">Deafened</SelectItem>
                    <SelectItem value="frightened">Frightened</SelectItem>
                    <SelectItem value="grappled">Grappled</SelectItem>
                    <SelectItem value="incapacitated">Incapacitated</SelectItem>
                    <SelectItem value="invisible">Invisible</SelectItem>
                    <SelectItem value="paralyzed">Paralyzed</SelectItem>
                    <SelectItem value="petrified">Petrified</SelectItem>
                    <SelectItem value="poisoned">Poisoned</SelectItem>
                    <SelectItem value="prone">Prone</SelectItem>
                    <SelectItem value="restrained">Restrained</SelectItem>
                    <SelectItem value="stunned">Stunned</SelectItem>
                    <SelectItem value="unconscious">Unconscious</SelectItem>
                  </SelectContent>
                </Select>
              </div>
              <div className="space-y-2">
                <Label>Duration (rounds)</Label>
                <Input
                  type="number"
                  placeholder="1"
                  value={conditionDuration}
                  onChange={(e) => setConditionDuration(e.target.value)}
                />
                <p className="text-xs text-muted-foreground">Use -1 for indefinite duration</p>
              </div>
              <div className="space-y-2">
                <Label>Notes (optional)</Label>
                <Input
                  placeholder="Additional details..."
                  value={conditionNotes}
                  onChange={(e) => setConditionNotes(e.target.value)}
                />
              </div>
              <div className="flex gap-2">
                <Button onClick={addCondition} className="flex-1">
                  Add Condition
                </Button>
                <Button variant="outline" onClick={() => setShowConditionDialog(false)}>
                  Cancel
                </Button>
              </div>
            </div>
          </DialogContent>
        </Dialog>
      </CardContent>
    </Card>
  );
};

import { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { DnD5eCharacter, DnD5eAbilityScores, Character } from "@/types/character";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Button } from "@/components/ui/button";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dices, Save, ArrowLeft } from "lucide-react";
import { toast } from "@/hooks/use-toast";

interface DnD5eCharacterFormProps {
  onBack: () => void;
  editMode?: boolean;
}

export const DnD5eCharacterForm = ({ onBack, editMode = false }: DnD5eCharacterFormProps) => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [character, setCharacter] = useState<Partial<DnD5eCharacter>>({
    name: "",
    race: "",
    class: "",
    level: 1,
    abilityScores: {
      strength: 10,
      dexterity: 10,
      constitution: 10,
      intelligence: 10,
      wisdom: 10,
      charisma: 10,
    },
  });

  useEffect(() => {
    if (editMode && id) {
      loadCharacter(id);
    }
  }, [editMode, id]);

  const loadCharacter = (characterId: string) => {
    const saved = localStorage.getItem("soloquest_characters");
    if (saved) {
      const characters: Character[] = JSON.parse(saved);
      const found = characters.find((c) => c.id === characterId);
      if (found && found.system === "dnd5e") {
        setCharacter(found.data as DnD5eCharacter);
      } else {
        toast({
          title: "Character Not Found",
          description: "The character you're trying to edit doesn't exist.",
          variant: "destructive",
        });
        navigate("/characters");
      }
    }
  };

  const races = [
    "Human", "Elf", "Dwarf", "Halfling", "Dragonborn", 
    "Gnome", "Half-Elf", "Half-Orc", "Tiefling"
  ];

  const classes = [
    "Barbarian", "Bard", "Cleric", "Druid", "Fighter", 
    "Monk", "Paladin", "Ranger", "Rogue", "Sorcerer", 
    "Warlock", "Wizard"
  ];

  const spellcastingClasses: { [key: string]: keyof DnD5eAbilityScores } = {
    "Bard": "charisma",
    "Cleric": "wisdom",
    "Druid": "wisdom",
    "Paladin": "charisma",
    "Ranger": "wisdom",
    "Sorcerer": "charisma",
    "Warlock": "charisma",
    "Wizard": "intelligence",
  };

  const abilityNames: (keyof DnD5eAbilityScores)[] = [
    "strength", "dexterity", "constitution", 
    "intelligence", "wisdom", "charisma"
  ];

  const getModifier = (score: number): string => {
    const mod = Math.floor((score - 10) / 2);
    return mod >= 0 ? `+${mod}` : `${mod}`;
  };

  const rollAbilityScore = () => {
    // Roll 4d6, drop lowest
    const rolls = Array.from({ length: 4 }, () => Math.floor(Math.random() * 6) + 1);
    rolls.sort((a, b) => a - b);
    return rolls.slice(1).reduce((sum, roll) => sum + roll, 0);
  };

  const rollAllAbilities = () => {
    const newScores: DnD5eAbilityScores = {
      strength: rollAbilityScore(),
      dexterity: rollAbilityScore(),
      constitution: rollAbilityScore(),
      intelligence: rollAbilityScore(),
      wisdom: rollAbilityScore(),
      charisma: rollAbilityScore(),
    };
    setCharacter({ ...character, abilityScores: newScores });
    toast({
      title: "Abilities Rolled!",
      description: "Your ability scores have been generated.",
    });
  };

  const updateAbilityScore = (ability: keyof DnD5eAbilityScores, value: string) => {
    const numValue = parseInt(value) || 0;
    const clampedValue = Math.min(Math.max(numValue, 1), 20);
    setCharacter({
      ...character,
      abilityScores: {
        ...character.abilityScores!,
        [ability]: clampedValue,
      },
    });
  };

  const handleSave = () => {
    if (!character.name || !character.race || !character.class) {
      toast({
        title: "Missing Information",
        description: "Please fill in name, race, and class.",
        variant: "destructive",
      });
      return;
    }

    const saved = localStorage.getItem("soloquest_characters");
    const characters: Character[] = saved ? JSON.parse(saved) : [];

    if (editMode && id) {
      // Update existing character
      const index = characters.findIndex((c) => c.id === id);
      if (index !== -1) {
        characters[index] = {
          ...characters[index],
          updatedAt: new Date().toISOString(),
          data: character as DnD5eCharacter,
        };
        localStorage.setItem("soloquest_characters", JSON.stringify(characters));
        toast({
          title: "Character Updated!",
          description: `${character.name} has been updated.`,
        });
        navigate(`/character/${id}`);
      }
    } else {
      // Create new character
      // Calculate HP (Constitution modifier + class base)
      const conMod = Math.floor(((character.abilityScores?.constitution || 10) - 10) / 2);
      const baseHP = 10; // Simplified, would vary by class
      const maxHP = baseHP + conMod;

      const isSpellcaster = character.class && character.class in spellcastingClasses;

      const completeCharacter: DnD5eCharacter = {
        id: character.id || crypto.randomUUID(),
        name: character.name,
        race: character.race,
        class: character.class,
        level: character.level || 1,
        abilityScores: character.abilityScores!,
        experiencePoints: character.experiencePoints || 0,
        hitPoints: character.hitPoints || {
          current: maxHP,
          max: maxHP,
        },
        inventory: character.inventory || [],
        skills: character.skills || {},
        savingThrows: character.savingThrows || {},
        ...(isSpellcaster && {
          spellcastingAbility: spellcastingClasses[character.class!],
          spellSlots: {
            level1: { current: 2, max: 2 },
            level2: { current: 0, max: 0 },
            level3: { current: 0, max: 0 },
            level4: { current: 0, max: 0 },
            level5: { current: 0, max: 0 },
            level6: { current: 0, max: 0 },
            level7: { current: 0, max: 0 },
            level8: { current: 0, max: 0 },
            level9: { current: 0, max: 0 },
          },
          preparedSpells: [],
        }),
      };

      characters.push({
        id: completeCharacter.id,
        system: "dnd5e",
        createdAt: new Date().toISOString(),
        updatedAt: new Date().toISOString(),
        data: completeCharacter,
      });
      localStorage.setItem("soloquest_characters", JSON.stringify(characters));

      toast({
        title: "Character Saved!",
        description: `${completeCharacter.name} has been created.`,
      });
      navigate("/characters");
    }
  };

  return (
    <div className="container max-w-4xl mx-auto p-6">
      <Button
        variant="ghost"
        onClick={onBack}
        className="mb-4"
      >
        <ArrowLeft className="w-4 h-4 mr-2" />
        Back to Systems
      </Button>

      <Card>
        <CardHeader>
          <CardTitle className="text-2xl">
            {editMode ? "Edit D&D 5e Character" : "Create D&D 5e Character"}
          </CardTitle>
          <CardDescription>
            {editMode
              ? "Update your adventurer's details"
              : "Build your adventurer for the quest ahead"}
          </CardDescription>
        </CardHeader>
        <CardContent className="space-y-6">
          {/* Basic Info */}
          <div className="grid md:grid-cols-2 gap-4">
            <div className="space-y-2">
              <Label htmlFor="name">Character Name</Label>
              <Input
                id="name"
                placeholder="Enter character name"
                value={character.name}
                onChange={(e) => setCharacter({ ...character, name: e.target.value })}
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="level">Level</Label>
              <Input
                id="level"
                type="number"
                min="1"
                max="20"
                value={character.level}
                onChange={(e) => setCharacter({ ...character, level: parseInt(e.target.value) || 1 })}
              />
            </div>
          </div>

          <div className="grid md:grid-cols-2 gap-4">
            <div className="space-y-2">
              <Label htmlFor="race">Race</Label>
              <Select value={character.race} onValueChange={(value) => setCharacter({ ...character, race: value })}>
                <SelectTrigger id="race" className="bg-popover">
                  <SelectValue placeholder="Select race" />
                </SelectTrigger>
                <SelectContent className="bg-popover z-50">
                  {races.map((race) => (
                    <SelectItem key={race} value={race}>
                      {race}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
            <div className="space-y-2">
              <Label htmlFor="class">Class</Label>
              <Select value={character.class} onValueChange={(value) => setCharacter({ ...character, class: value })}>
                <SelectTrigger id="class" className="bg-popover">
                  <SelectValue placeholder="Select class" />
                </SelectTrigger>
                <SelectContent className="bg-popover z-50">
                  {classes.map((cls) => (
                    <SelectItem key={cls} value={cls}>
                      {cls}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
          </div>

          {/* Ability Scores */}
          <div>
            <div className="flex items-center justify-between mb-4">
              <Label className="text-lg">Ability Scores</Label>
              <Button onClick={rollAllAbilities} variant="outline" size="sm">
                <Dices className="w-4 h-4 mr-2" />
                Roll All (4d6 drop lowest)
              </Button>
            </div>
            <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
              {abilityNames.map((ability) => (
                <div key={ability} className="space-y-2">
                  <Label htmlFor={ability} className="capitalize">
                    {ability}
                  </Label>
                  <div className="flex items-center gap-2">
                    <Input
                      id={ability}
                      type="number"
                      min="1"
                      max="20"
                      value={character.abilityScores?.[ability] || 10}
                      onChange={(e) => updateAbilityScore(ability, e.target.value)}
                      className="w-20"
                    />
                    <span className="text-sm font-medium text-muted-foreground w-10">
                      {getModifier(character.abilityScores?.[ability] || 10)}
                    </span>
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Save Button */}
          <div className="pt-4">
            <Button onClick={handleSave} size="lg" className="w-full">
              <Save className="w-4 h-4 mr-2" />
              {editMode ? "Update Character" : "Save Character"}
            </Button>
          </div>
        </CardContent>
      </Card>
    </div>
  );
};

