  const [deleteDialogOpen, setDeleteDialogOpen] = useState(false);
  const [characterToDelete, setCharacterToDelete] = useState<string | null>(null);
  const navigate = useNavigate();

  useEffect(() => {
    loadCharacters();
  }, []);

  useEffect(() => {
    filterCharacters();
  }, [characters, searchQuery, systemFilter]);

  const loadCharacters = () => {
    const saved = localStorage.getItem("soloquest_characters");
    if (saved) {
      const parsed = JSON.parse(saved);
      setCharacters(parsed);
    }
  };

  const filterCharacters = () => {
    let filtered = [...characters];

    // Filter by system
    if (systemFilter !== "all") {
      filtered = filtered.filter((char) => char.system === systemFilter);
    }

    // Filter by search query
    if (searchQuery) {
      filtered = filtered.filter((char) =>
        char.data.name.toLowerCase().includes(searchQuery.toLowerCase())
      );
    }

    setFilteredCharacters(filtered);
  };

  const handleDelete = (id: string) => {
    setCharacterToDelete(id);
    setDeleteDialogOpen(true);
  };

  const confirmDelete = () => {
    if (characterToDelete) {
      const updated = characters.filter((char) => char.id !== characterToDelete);
      localStorage.setItem("soloquest_characters", JSON.stringify(updated));
      setCharacters(updated);
      toast({
        title: "Character Deleted",
        description: "The character has been removed.",
      });
    }
    setDeleteDialogOpen(false);
    setCharacterToDelete(null);
  };

  const getSystemLabel = (system: string) => {
    switch (system) {
      case "dnd5e":
        return "D&D 5e";
      case "dragonbane":
        return "Dragonbane";
      case "cyberpunk":
        return "Cyberpunk Red";
      default:
        return system;
    }
  };

  return (
    <div className="min-h-screen bg-background">
      <div className="container max-w-6xl mx-auto p-6">
        <div className="flex items-center justify-between mb-6">
          <div>
            <h1 className="text-3xl font-bold mb-2">My Characters</h1>
            <p className="text-muted-foreground">
              Manage your adventurers across different game systems
            </p>
          </div>
          <div className="flex gap-2">
            <Button variant="outline" onClick={() => navigate("/dice")}>
              <Dices className="w-4 h-4 mr-2" />
              Dice Roller
            </Button>
            <Button onClick={() => navigate("/")} size="lg">
              <Plus className="w-4 h-4 mr-2" />
              New Character
            </Button>
          </div>
        </div>

        {/* Filters */}
        <Card className="mb-6">
          <CardContent className="pt-6">
            <div className="grid md:grid-cols-2 gap-4">
              <div className="space-y-2">
                <label className="text-sm font-medium">Search</label>
                <div className="relative">
                  <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-muted-foreground" />
                  <Input
                    placeholder="Search by character name..."
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    className="pl-10"
                  />
                </div>
              </div>
              <div className="space-y-2">
                <label className="text-sm font-medium">Game System</label>
                <Select value={systemFilter} onValueChange={setSystemFilter}>
                  <SelectTrigger className="bg-popover">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent className="bg-popover z-50">
                    <SelectItem value="all">All Systems</SelectItem>
                    <SelectItem value="dnd5e">D&D 5th Edition</SelectItem>
                    <SelectItem value="dragonbane">Dragonbane</SelectItem>
                    <SelectItem value="cyberpunk">Cyberpunk Red</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Character Grid */}
        {filteredCharacters.length === 0 ? (
          <Card>
            <CardContent className="py-12 text-center">
              <p className="text-muted-foreground mb-4">
                {characters.length === 0
                  ? "No characters yet. Create your first adventurer!"
                  : "No characters match your filters."}
              </p>
              {characters.length === 0 && (
                <Button onClick={() => navigate("/")}>
                  <Plus className="w-4 h-4 mr-2" />
                  Create Character
                </Button>
              )}
            </CardContent>
          </Card>
        ) : (
          <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
            {filteredCharacters.map((character) => (
              <Card key={character.id} className="hover:shadow-lg transition-shadow">
                <CardHeader>
                  <div className="flex items-start justify-between mb-2">
                    <span className="text-xs font-medium px-2 py-1 rounded-full bg-primary/10 text-primary">
                      {getSystemLabel(character.system)}
                    </span>
                  </div>
                  <CardTitle className="text-xl">{character.data.name}</CardTitle>
                  <CardDescription>
                    {character.system === "dnd5e" && (
                      <>
                        {character.data.race} {character.data.class} тАв Level{" "}
                        {character.data.level}
                      </>
                    )}
                  </CardDescription>
                </CardHeader>
                <CardContent>
                  <div className="flex gap-2">
                    <Button
                      variant="outline"
                      size="sm"
                      className="flex-1"
                      onClick={() => navigate(`/character/${character.id}`)}
                    >
                      <Eye className="w-4 h-4 mr-1" />
                      View
                    </Button>
                    <Button
                      variant="outline"
                      size="sm"
                      className="flex-1"
                      onClick={() => navigate(`/character/${character.id}/edit`)}
                    >
                      <Edit className="w-4 h-4 mr-1" />
                      Edit
                    </Button>
                    <Button
                      variant="destructive"
                      size="sm"
                      onClick={() => handleDelete(character.id)}
                    >
                      <Trash2 className="w-4 h-4" />
                    </Button>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        )}
      </div>

      {/* Delete Confirmation Dialog */}
      <AlertDialog open={deleteDialogOpen} onOpenChange={setDeleteDialogOpen}>
        <AlertDialogContent>
          <AlertDialogHeader>
            <AlertDialogTitle>Delete Character?</AlertDialogTitle>
            <AlertDialogDescription>
              This action cannot be undone. This will permanently delete the character
              from your collection.
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter>
            <AlertDialogCancel>Cancel</AlertDialogCancel>
            <AlertDialogAction onClick={confirmDelete} className="bg-destructive text-destructive-foreground hover:bg-destructive/90">
              Delete
            </AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>
    </div>
  );
};

import { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { Character, DnD5eCharacter, InventoryItem, SkillProficiency, SavingThrowProficiency, SpellSlots, PreparedSpell, DeathSaves, Condition } from "@/types/character";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Progress } from "@/components/ui/progress";
import { Badge } from "@/components/ui/badge";
import { InventoryManager } from "@/components/InventoryManager";
import { SkillsManager } from "@/components/SkillsManager";
import { SavingThrowsManager } from "@/components/SavingThrowsManager";
import { SpellsManager } from "@/components/SpellsManager";
import { ArrowLeft, Edit, Dices, Heart, Skull, Plus, Minus, Moon, Sun, X, Circle, CheckCircle2, XCircle, BookOpen, Swords } from "lucide-react";
import { toast } from "@/hooks/use-toast";

export const CharacterView = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [character, setCharacter] = useState<Character | null>(null);
  const [hpAdjustment, setHpAdjustment] = useState<string>("");

  useEffect(() => {
    if (id) {
      loadCharacter(id);
    }
  }, [id]);

  const loadCharacter = (characterId: string) => {
    const saved = localStorage.getItem("soloquest_characters");
    if (saved) {
      const characters: Character[] = JSON.parse(saved);
      const found = characters.find((c) => c.id === characterId);
      if (found) {
        setCharacter(found);
      } else {
        toast({
          title: "Character Not Found",
          description: "The character you're looking for doesn't exist.",
          variant: "destructive",
        });
        navigate("/characters");
      }
    }
  };

  const saveCharacter = (updatedCharacter: Character) => {
    const saved = localStorage.getItem("soloquest_characters");
    if (saved) {
      const characters: Character[] = JSON.parse(saved);
      const index = characters.findIndex((c) => c.id === updatedCharacter.id);
      if (index !== -1) {
        characters[index] = {
          ...updatedCharacter,
          updatedAt: new Date().toISOString(),
        };
        localStorage.setItem("soloquest_characters", JSON.stringify(characters));
        setCharacter(characters[index]);
      }
    }
  };

  const updateHP = (newCurrent: number) => {
    if (!character) return;

    const dndChar = character.data as DnD5eCharacter;
    const clampedHP = Math.max(0, Math.min(newCurrent, dndChar.hitPoints.max));

    const updatedCharacter: Character = {
      ...character,
      data: {
        ...dndChar,
        hitPoints: {
          ...dndChar.hitPoints,
          current: clampedHP,
        },
      },
    };

    saveCharacter(updatedCharacter);

    if (clampedHP === 0) {
      toast({
        title: "Character Unconscious!",
        description: `${dndChar.name} has fallen to 0 HP!`,
        variant: "destructive",
      });
    }
  };

  const takeDamage = () => {
    const amount = parseInt(hpAdjustment) || 0;
    if (amount <= 0) {
      toast({
        title: "Invalid Amount",
        description: "Please enter a positive number for damage.",
        variant: "destructive",
      });
      return;
    }

    const dndChar = character?.data as DnD5eCharacter;
    updateHP(dndChar.hitPoints.current - amount);
    setHpAdjustment("");

    toast({
      title: "Damage Taken",
      description: `${dndChar.name} took ${amount} damage!`,
    });
  };

  const heal = () => {
    const amount = parseInt(hpAdjustment) || 0;
    if (amount <= 0) {
      toast({
        title: "Invalid Amount",
        description: "Please enter a positive number for healing.",
        variant: "destructive",
      });
      return;
    }

    const dndChar = character?.data as DnD5eCharacter;
    updateHP(dndChar.hitPoints.current + amount);
    setHpAdjustment("");

    toast({
      title: "Healed",
      description: `${dndChar.name} recovered ${amount} HP!`,
    });
  };

  const restoreToMax = () => {
    const dndChar = character?.data as DnD5eCharacter;
    updateHP(dndChar.hitPoints.max);

    toast({
      title: "Fully Healed",
      description: `${dndChar.name} is back to full health!`,
    });
  };

  const adjustHP = (amount: number) => {
    const dndChar = character?.data as DnD5eCharacter;
    updateHP(dndChar.hitPoints.current + amount);
  };

  const getHPColor = (current: number, max: number) => {
    const percentage = (current / max) * 100;
    if (percentage > 66) return "text-green-600 dark:text-green-400";
    if (percentage > 33) return "text-yellow-600 dark:text-yellow-400";
    return "text-red-600 dark:text-red-400";
  };

  const getHPProgressColor = (current: number, max: number) => {
    const percentage = (current / max) * 100;
    if (percentage > 66) return "bg-green-600";
    if (percentage > 33) return "bg-yellow-600";
    return "bg-red-600";
  };

  const updateInventory = (newInventory: InventoryItem[]) => {
    if (!character) return;

    const dndChar = character.data as DnD5eCharacter;
    const updatedCharacter: Character = {
      ...character,
      data: {
        ...dndChar,
        inventory: newInventory,
      },
    };

    saveCharacter(updatedCharacter);
  };

  const updateSkills = (newSkills: SkillProficiency) => {
    if (!character) return;

    const dndChar = character.data as DnD5eCharacter;
    const updatedCharacter: Character = {
      ...character,
      data: {
        ...dndChar,
        skills: newSkills,
      },
    };

    saveCharacter(updatedCharacter);
  };

  const updateSavingThrows = (newSavingThrows: SavingThrowProficiency) => {
    if (!character) return;

    const dndChar = character.data as DnD5eCharacter;
    const updatedCharacter: Character = {
      ...character,
      data: {
        ...dndChar,
        savingThrows: newSavingThrows,
      },
    };

    saveCharacter(updatedCharacter);
  };

  const updateSpellSlots = (newSpellSlots: SpellSlots) => {
    if (!character) return;

    const dndChar = character.data as DnD5eCharacter;
    const updatedCharacter: Character = {
      ...character,
      data: {
        ...dndChar,
        spellSlots: newSpellSlots,
      },
    };

    saveCharacter(updatedCharacter);
  };

  const updatePreparedSpells = (newPreparedSpells: PreparedSpell[]) => {
    if (!character) return;

    const dndChar = character.data as DnD5eCharacter;
    const updatedCharacter: Character = {
      ...character,
      data: {
        ...dndChar,
        preparedSpells: newPreparedSpells,
      },
    };

    saveCharacter(updatedCharacter);
  };

  const getCarryingCapacity = (strength: number): number => {
    return strength * 15; // D&D 5e rule: Strength * 15 lbs
  };

  const getModifier = (score: number): string => {
    const mod = Math.floor((score - 10) / 2);
    return mod >= 0 ? `+${mod}` : `${mod}`;
  };

  const updateDeathSaves = (successes: number, failures: number) => {
    if (!character) return;
    const dndChar = character.data as DnD5eCharacter;
    const updatedCharacter: Character = {
      ...character,
      data: {
        ...dndChar,
        deathSaves: { successes, failures },
      },
    };
    saveCharacter(updatedCharacter);
  };

  const toggleCondition = (condition: Condition) => {
    if (!character) return;
    const dndChar = character.data as DnD5eCharacter;
    const currentConditions = dndChar.conditions || [];
    const hasCondition = currentConditions.includes(condition);

    const updatedConditions = hasCondition
      ? currentConditions.filter(c => c !== condition)
      : [...currentConditions, condition];

    const updatedCharacter: Character = {
      ...character,
      data: {
        ...dndChar,
        conditions: updatedConditions,
      },
    };
    saveCharacter(updatedCharacter);
  };

  const shortRest = () => {
    if (!character) return;
    const dndChar = character.data as DnD5eCharacter;

    // Spend hit dice to heal
    const conMod = Math.floor((dndChar.abilityScores.constitution - 10) / 2);
    const hitDice = dndChar.hitDice || { current: dndChar.level, max: dndChar.level };

    if (hitDice.current > 0) {
      // Roll hit dice (simplified: use average)
      const classHitDie = 8; // Simplified, should be based on class
      const healAmount = Math.floor(classHitDie / 2) + 1 + conMod;
      const newHP = Math.min(dndChar.hitPoints.current + healAmount, dndChar.hitPoints.max);

      const updatedCharacter: Character = {
        ...character,
        data: {
          ...dndChar,
          hitPoints: { ...dndChar.hitPoints, current: newHP },
          hitDice: { ...hitDice, current: hitDice.current - 1 },
        },
      };
      saveCharacter(updatedCharacter);

      toast({
        title: "Short Rest Complete",
        description: `Spent 1 hit die and healed ${healAmount} HP. ${hitDice.current - 1} hit dice remaining.`,
      });
    } else {
      toast({
        title: "No Hit Dice",
        description: "You need hit dice to heal during a short rest.",
        variant: "destructive",
      });
    }
  };

  const longRest = () => {
    if (!character) return;
    const dndChar = character.data as DnD5eCharacter;

    // Restore HP to max
    const newHP = dndChar.hitPoints.max;

    // Restore spell slots to max
    const restoredSpellSlots = dndChar.spellSlots ? {
      level1: { ...dndChar.spellSlots.level1, current: dndChar.spellSlots.level1.max },
      level2: { ...dndChar.spellSlots.level2, current: dndChar.spellSlots.level2.max },
      level3: { ...dndChar.spellSlots.level3, current: dndChar.spellSlots.level3.max },
      level4: { ...dndChar.spellSlots.level4, current: dndChar.spellSlots.level4.max },
      level5: { ...dndChar.spellSlots.level5, current: dndChar.spellSlots.level5.max },
      level6: { ...dndChar.spellSlots.level6, current: dndChar.spellSlots.level6.max },
      level7: { ...dndChar.spellSlots.level7, current: dndChar.spellSlots.level7.max },
      level8: { ...dndChar.spellSlots.level8, current: dndChar.spellSlots.level8.max },
      level9: { ...dndChar.spellSlots.level9, current: dndChar.spellSlots.level9.max },
    } : undefined;

    // Restore hit dice (at least half)
    const hitDice = dndChar.hitDice || { current: dndChar.level, max: dndChar.level };
    const restoredHitDice = {
      ...hitDice,
      current: Math.min(hitDice.current + Math.max(1, Math.floor(hitDice.max / 2)), hitDice.max),
    };

    // Clear death saves
    const clearedDeathSaves = { successes: 0, failures: 0 };

    // Reduce exhaustion by 1 (if any)
    const newExhaustion = Math.max(0, (dndChar.exhaustionLevel || 0) - 1);

    const updatedCharacter: Character = {
      ...character,
      data: {
        ...dndChar,
        hitPoints: { ...dndChar.hitPoints, current: newHP },
        spellSlots: restoredSpellSlots,
        hitDice: restoredHitDice,
        deathSaves: clearedDeathSaves,
        exhaustionLevel: newExhaustion,
      },
    };
    saveCharacter(updatedCharacter);

    toast({
      title: "Long Rest Complete",
      description: "HP, spell slots, and hit dice restored. Death saves cleared.",
    });
  };

  if (!character) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <p className="text-muted-foreground">Loading character...</p>
      </div>
    );
  }

  const dndCharacter = character.data as DnD5eCharacter;

  return (
    <div className="min-h-screen bg-background">
      <div className="container max-w-4xl mx-auto p-6">
        <div className="flex items-center justify-between mb-6">
          <Button variant="ghost" onClick={() => navigate("/characters")}>
            <ArrowLeft className="w-4 h-4 mr-2" />
            Back to Characters
          </Button>
          <div className="flex gap-2">
            <Button variant="outline" onClick={() => navigate(`/character/${id}/combat`)}>
              <Swords className="w-4 h-4 mr-2" />
              Combat
            </Button>
            <Button variant="outline" onClick={() => navigate(`/character/${id}/journal`)}>
              <BookOpen className="w-4 h-4 mr-2" />
              Journal
            </Button>
            <Button variant="outline" onClick={() => navigate("/dice")}>
              <Dices className="w-4 h-4 mr-2" />
              Dice Roller
            </Button>
            <Button onClick={() => navigate(`/character/${id}/edit`)}>
              <Edit className="w-4 h-4 mr-2" />
              Edit Character
            </Button>
          </div>
        </div>

        <Card className="mb-6">
          <CardHeader>
            <div className="flex items-start justify-between">
              <div>
                <CardTitle className="text-3xl mb-2">{dndCharacter.name}</CardTitle>
                <CardDescription className="text-lg">
                  {dndCharacter.race} {dndCharacter.class} тАв Level {dndCharacter.level}
                </CardDescription>
              </div>
              <span className="text-xs font-medium px-3 py-1 rounded-full bg-primary/10 text-primary">
                D&D 5e
              </span>
            </div>
          </CardHeader>
        </Card>

        {/* Rest Mechanics */}
        <div className="grid md:grid-cols-2 gap-4 mb-6">
          <Button variant="outline" size="lg" onClick={shortRest} className="h-auto py-4">
            <div className="flex flex-col items-center gap-2">
              <Moon className="w-6 h-6" />
              <div>
                <div className="font-semibold">Short Rest</div>
                <div className="text-xs text-muted-foreground">Spend hit dice to heal</div>
              </div>
            </div>
          </Button>
          <Button variant="outline" size="lg" onClick={longRest} className="h-auto py-4">
            <div className="flex flex-col items-center gap-2">
              <Sun className="w-6 h-6" />
              <div>
                <div className="font-semibold">Long Rest</div>
                <div className="text-xs text-muted-foreground">Restore HP, spells, hit dice</div>
              </div>
            </div>
          </Button>
        </div>

        {/* Hit Points & Death Saves */}
        <Card className="mb-6">
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Heart className="w-5 h-5" />
              Hit Points
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            {/* HP Display */}
            <div className="text-center">
              <div className={`text-5xl font-bold mb-2 ${getHPColor(dndCharacter.hitPoints.current, dndCharacter.hitPoints.max)}`}>
                {dndCharacter.hitPoints.current}
                <span className="text-2xl text-muted-foreground"> / {dndCharacter.hitPoints.max}</span>
              </div>
              <Progress 
                value={(dndCharacter.hitPoints.current / dndCharacter.hitPoints.max) * 100} 
                className="h-3"
                indicatorClassName={getHPProgressColor(dndCharacter.hitPoints.current, dndCharacter.hitPoints.max)}
              />
            </div>

            {/* Quick HP Adjustment */}
            <div className="flex gap-2 justify-center">
              <Button 
                variant="outline" 
                size="icon"
                onClick={() => adjustHP(-5)}
                disabled={dndCharacter.hitPoints.current === 0}
              >
                -5
              </Button>
              <Button 
                variant="outline" 
                size="icon"
                onClick={() => adjustHP(-1)}
                disabled={dndCharacter.hitPoints.current === 0}
              >
                <Minus className="w-4 h-4" />
              </Button>
              <Button 
                variant="outline" 
                size="icon"
                onClick={() => adjustHP(1)}
                disabled={dndCharacter.hitPoints.current >= dndCharacter.hitPoints.max}
              >
                <Plus className="w-4 h-4" />
              </Button>
              <Button 
                variant="outline" 
                size="icon"
                onClick={() => adjustHP(5)}
                disabled={dndCharacter.hitPoints.current >= dndCharacter.hitPoints.max}
              >
                +5
              </Button>
            </div>

            {/* HP Management */}
            <div className="space-y-3 pt-4 border-t">
              <div className="flex gap-2">
                <Input
                  type="number"
                  min="0"
                  placeholder="Amount..."
                  value={hpAdjustment}
                  onChange={(e) => setHpAdjustment(e.target.value)}
                  className="flex-1"
                />
              </div>
              <div className="grid grid-cols-3 gap-2">
                <Button 
                  variant="destructive" 
                  onClick={takeDamage}
                  disabled={!hpAdjustment || dndCharacter.hitPoints.current === 0}
                >
                  <Skull className="w-4 h-4 mr-2" />
                  Damage
                </Button>
                <Button 
                  variant="default" 
                  onClick={heal}
                  disabled={!hpAdjustment || dndCharacter.hitPoints.current >= dndCharacter.hitPoints.max}
                >
                  <Heart className="w-4 h-4 mr-2" />
                  Heal
                </Button>
                <Button 
                  variant="secondary" 
                  onClick={restoreToMax}
                  disabled={dndCharacter.hitPoints.current >= dndCharacter.hitPoints.max}
                >
                  Full Heal
                </Button>
              </div>
            </div>

            {/* Hit Dice */}
            <div className="pt-4 border-t">
              <div className="flex items-center justify-between">
                <span className="text-sm font-medium">Hit Dice</span>
                <span className="text-lg font-semibold">
                  {dndCharacter.hitDice?.current ?? dndCharacter.level} / {dndCharacter.hitDice?.max ?? dndCharacter.level}
                </span>
              </div>
              <p className="text-xs text-muted-foreground mt-1">
                Use during short rest to heal
              </p>
            </div>

            {/* Death Saves (shown when HP is 0) */}
            {dndCharacter.hitPoints.current === 0 && (
              <div className="pt-4 border-t">
                <h3 className="text-sm font-semibold mb-3 flex items-center gap-2">
                  <Skull className="w-4 h-4" />
                  Death Saves
                </h3>
                <div className="space-y-3">
                  <div className="flex items-center justify-between">
                    <span className="text-sm">Successes</span>
                    <div className="flex gap-2">
                      {[1, 2, 3].map((i) => (
                        <button
                          key={`success-${i}`}
                          onClick={() => {
                            const deathSaves = dndCharacter.deathSaves || { successes: 0, failures: 0 };
                            updateDeathSaves(
                              deathSaves.successes === i ? i - 1 : i,
                              deathSaves.failures
                            );
                          }}
                          className="hover:scale-110 transition-transform"
                        >
                          {(dndCharacter.deathSaves?.successes || 0) >= i ? (
                            <CheckCircle2 className="w-6 h-6 text-green-500" />
                          ) : (
                            <Circle className="w-6 h-6 text-muted-foreground" />
                          )}
                        </button>
                      ))}
                    </div>
                  </div>
                  <div className="flex items-center justify-between">
                    <span className="text-sm">Failures</span>
                    <div className="flex gap-2">
                      {[1, 2, 3].map((i) => (
                        <button
                          key={`failure-${i}`}
                          onClick={() => {
                            const deathSaves = dndCharacter.deathSaves || { successes: 0, failures: 0 };
                            updateDeathSaves(
                              deathSaves.successes,
                              deathSaves.failures === i ? i - 1 : i
                            );
                          }}
                          className="hover:scale-110 transition-transform"
                        >
                          {(dndCharacter.deathSaves?.failures || 0) >= i ? (
                            <XCircle className="w-6 h-6 text-destructive" />
                          ) : (
                            <Circle className="w-6 h-6 text-muted-foreground" />
                          )}
                        </button>
                      ))}
                    </div>
                  </div>
                </div>
              </div>
            )}
          </CardContent>
        </Card>

        {/* Conditions */}
        <Card className="mb-6">
          <CardHeader>
            <CardTitle>Conditions</CardTitle>
            <CardDescription>Track active status effects</CardDescription>
          </CardHeader>
          <CardContent>
            <div className="flex flex-wrap gap-2">
              {(["Blinded", "Charmed", "Deafened", "Frightened", "Grappled", "Incapacitated", 
                "Invisible", "Paralyzed", "Petrified", "Poisoned", "Prone", "Restrained", 
                "Stunned", "Unconscious"] as Condition[]).map((condition) => {
                const isActive = dndCharacter.conditions?.includes(condition);
                return (
                  <Badge
                    key={condition}
                    variant={isActive ? "default" : "outline"}
                    className="cursor-pointer hover:scale-105 transition-transform"
                    onClick={() => toggleCondition(condition)}
                  >
                    {condition}
                    {isActive && <X className="w-3 h-3 ml-1" />}
                  </Badge>
                );
              })}
            </div>
            {(dndCharacter.exhaustionLevel || 0) > 0 && (
              <div className="mt-4 pt-4 border-t">
                <div className="flex items-center justify-between">
                  <span className="text-sm font-medium">Exhaustion Level</span>
                  <Badge variant="destructive">{dndCharacter.exhaustionLevel}</Badge>
                </div>
              </div>
            )}
          </CardContent>
        </Card>

        {/* Ability Scores & Saving Throws */}
        <div className="grid md:grid-cols-2 gap-6 mb-6">
          <Card>
            <CardHeader>
              <CardTitle>Ability Scores</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-2 gap-4">
                {Object.entries(dndCharacter.abilityScores).map(([ability, score]) => (
                  <div key={ability} className="text-center p-4 rounded-lg bg-muted">
                    <div className="text-sm font-medium text-muted-foreground capitalize mb-1">
                      {ability}
                    </div>
                    <div className="text-3xl font-bold">{score}</div>
                    <div className="text-sm text-muted-foreground mt-1">
                      {getModifier(score)}
                    </div>
                  </div>
                ))}
              </div>
            </CardContent>
          </Card>

          <SavingThrowsManager
            abilityScores={dndCharacter.abilityScores}
            level={dndCharacter.level}
            savingThrows={dndCharacter.savingThrows || {}}
            onUpdateSavingThrows={updateSavingThrows}
            readOnly={true}
          />
        </div>

        {/* Skills */}
        <div className="mb-6">
          <SkillsManager
            abilityScores={dndCharacter.abilityScores}
            level={dndCharacter.level}
            skills={dndCharacter.skills || {}}
            onUpdateSkills={updateSkills}
            readOnly={true}
          />
        </div>

        {/* Spells (if applicable) */}
        {dndCharacter.spellcastingAbility && (
          <div className="mb-6">
            <SpellsManager
              spellSlots={dndCharacter.spellSlots || {
                level1: { current: 0, max: 0 },
                level2: { current: 0, max: 0 },
                level3: { current: 0, max: 0 },
                level4: { current: 0, max: 0 },
                level5: { current: 0, max: 0 },
                level6: { current: 0, max: 0 },
                level7: { current: 0, max: 0 },
                level8: { current: 0, max: 0 },
                level9: { current: 0, max: 0 },
              }}
              preparedSpells={dndCharacter.preparedSpells || []}
              spellcastingAbility={dndCharacter.spellcastingAbility}
              abilityScores={dndCharacter.abilityScores}
              level={dndCharacter.level}
              onUpdateSpellSlots={updateSpellSlots}
              onUpdatePreparedSpells={updatePreparedSpells}
            />
          </div>
        )}

        {/* Inventory Management */}
        <InventoryManager
          inventory={dndCharacter.inventory || []}
          carryingCapacity={getCarryingCapacity(dndCharacter.abilityScores.strength)}
          onUpdateInventory={updateInventory}
        />

        {/* Additional Info */}
        <div className="grid md:grid-cols-2 gap-6 mt-6">
          <Card>
            <CardHeader>
              <CardTitle>Character Details</CardTitle>
            </CardHeader>
            <CardContent className="space-y-3">
              <div>
                <span className="text-sm text-muted-foreground">Experience Points:</span>
                <p className="font-medium">{dndCharacter.experiencePoints} XP</p>
              </div>
              {dndCharacter.alignment && (
                <div>
                  <span className="text-sm text-muted-foreground">Alignment:</span>
                  <p className="font-medium">{dndCharacter.alignment}</p>
                </div>
              )}
              {dndCharacter.background && (
                <div>
                  <span className="text-sm text-muted-foreground">Background:</span>
                  <p className="font-medium">{dndCharacter.background}</p>
                </div>
              )}
            </CardContent>
          </Card>

          <Card>
            <CardHeader>
              <CardTitle>Character Info</CardTitle>
            </CardHeader>
            <CardContent className="space-y-3">
              <div>
                <span className="text-sm text-muted-foreground">Created:</span>
                <p className="font-medium">
                  {new Date(character.createdAt).toLocaleDateString()}
                </p>
              </div>
              <div>
                <span className="text-sm text-muted-foreground">Last Updated:</span>
                <p className="font-medium">
                  {new Date(character.updatedAt).toLocaleDateString()}
                </p>
              </div>
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
};

import { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { Character, DnD5eCharacter } from "@/types/character";
import { Combatant, CombatantType } from "@/types/combat";
import { NPC } from "@/types/npc";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { CombatParticipant } from "@/components/CombatParticipant";
import { ArrowLeft, Plus, Swords, RotateCcw, ChevronRight, Dices, RefreshCw, Users } from "lucide-react";
import { toast } from "@/hooks/use-toast";

export const CombatTracker = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [character, setCharacter] = useState<Character | null>(null);
  const [combatants, setCombatants] = useState<Combatant[]>([]);
  const [currentTurn, setCurrentTurn] = useState(0);
  const [round, setRound] = useState(1);
  const [showAddForm, setShowAddForm] = useState(false);
  const [showNPCDialog, setShowNPCDialog] = useState(false);
  const [savedNPCs, setSavedNPCs] = useState<NPC[]>([]);

  const [newCombatant, setNewCombatant] = useState({
    name: "",
    type: "enemy" as CombatantType,
    initiative: "",
    initiativeBonus: "",
    hp: "",
    ac: "",
  });

  useEffect(() => {
    if (id) {
      loadCharacter(id);
    }
    loadNPCs();
  }, [id]);

  const loadNPCs = () => {
    const saved = localStorage.getItem("soloquest_npcs");
    if (saved) {
      setSavedNPCs(JSON.parse(saved));
    }
  };

  const loadCharacter = (characterId: string) => {
    const saved = localStorage.getItem("soloquest_characters");
    if (saved) {
      const characters: Character[] = JSON.parse(saved);
      const found = characters.find((c) => c.id === characterId);
      if (found) {
        setCharacter(found);
      }
    }
  };

  const addPlayerCharacter = () => {
    if (!character) return;

    const dndChar = character.data as DnD5eCharacter;
    const existingPlayer = combatants.find((c) => c.characterId === character.id);

    if (existingPlayer) {
      toast({
        title: "Already Added",
        description: "Your character is already in the initiative order.",
        variant: "destructive",
      });
      return;
    }

    const dexModifier = Math.floor((dndChar.abilityScores.dexterity - 10) / 2);

    const playerCombatant: Combatant = {
      id: crypto.randomUUID(),
      name: dndChar.name,
      type: "player",
      initiative: 0,
      initiativeBonus: dexModifier,
      hitPoints: {
        current: dndChar.hitPoints.current,
        max: dndChar.hitPoints.max,
      },
      armorClass: 10 + dexModifier, // Base AC + DEX mod
      characterId: character.id,
    };

    setCombatants([...combatants, playerCombatant]);
    toast({
      title: "Character Added",
      description: `${dndChar.name} has been added to combat.`,
    });
  };

  const addCombatant = () => {
    if (!newCombatant.name || !newCombatant.hp || !newCombatant.ac) {
      toast({
        title: "Missing Information",
        description: "Please fill in all required fields.",
        variant: "destructive",
      });
      return;
    }

    const combatant: Combatant = {
      id: crypto.randomUUID(),
      name: newCombatant.name,
      type: newCombatant.type,
      initiative: parseInt(newCombatant.initiative) || 0,
      initiativeBonus: newCombatant.initiativeBonus ? parseInt(newCombatant.initiativeBonus) : 0,
      hitPoints: {
        current: parseInt(newCombatant.hp),
        max: parseInt(newCombatant.hp),
      },
      armorClass: parseInt(newCombatant.ac),
    };

    setCombatants([...combatants, combatant]);
    setNewCombatant({ name: "", type: "enemy", initiative: "", initiativeBonus: "", hp: "", ac: "" });
    setShowAddForm(false);

    toast({
      title: "Combatant Added",
      description: `${combatant.name} has been added to combat.`,
    });
  };

  const addNPCToCombat = (npc: NPC) => {
    const combatant: Combatant = {
      id: crypto.randomUUID(),
      name: npc.name,
      type: npc.type === "neutral" ? "enemy" : npc.type,
      initiative: 0,
      initiativeBonus: npc.initiativeBonus || 0,
      hitPoints: {
        current: npc.hitPoints,
        max: npc.hitPoints,
      },
      armorClass: npc.armorClass,
    };

    setCombatants([...combatants, combatant]);
    setShowNPCDialog(false);

    toast({
      title: "NPC Added",
      description: `${npc.name} has been added to combat.`,
    });
  };

  const rollInitiativeForAll = () => {
    const updated = combatants.map((c) => ({
      ...c,
      initiative: Math.floor(Math.random() * 20) + 1 + (c.initiativeBonus || 0),
    }));
    sortAndSetCombatants(updated);

    toast({
      title: "Initiative Rolled",
      description: "All combatants have rolled initiative!",
    });
  };

  const sortAndSetCombatants = (combatantList: Combatant[]) => {
    const sorted = [...combatantList].sort((a, b) => b.initiative - a.initiative);
    setCombatants(sorted);
  };

  const updateCombatantHP = (id: string, newHP: number) => {
    const updated = combatants.map((c) =>
      c.id === id ? { ...c, hitPoints: { ...c.hitPoints, current: newHP } } : c
    );
    setCombatants(updated);

    // Sync player character HP back to character data
    const combatant = updated.find((c) => c.id === id);
    if (combatant?.characterId && character) {
      const dndChar = character.data as DnD5eCharacter;
      const updatedCharacter: Character = {
        ...character,
        data: {
          ...dndChar,
          hitPoints: {
            ...dndChar.hitPoints,
            current: newHP,
          },
        },
      };

      const saved = localStorage.getItem("soloquest_characters");
      if (saved) {
        const characters: Character[] = JSON.parse(saved);
        const index = characters.findIndex((c) => c.id === character.id);
        if (index !== -1) {
          characters[index] = updatedCharacter;
          localStorage.setItem("soloquest_characters", JSON.stringify(characters));
          setCharacter(updatedCharacter);
        }
      }
    }
  };

  const updateCombatantConditions = (id: string, conditions: any[]) => {
    const updated = combatants.map((c) => (c.id === id ? { ...c, conditions } : c));
    setCombatants(updated);
  };

  const decrementConditionDurations = () => {
    const updated = combatants.map((combatant) => {
      if (!combatant.conditions || combatant.conditions.length === 0) return combatant;

      const updatedConditions = combatant.conditions
        .map((condition) => {
          if (condition.duration === -1) return condition; // Indefinite
          return { ...condition, duration: condition.duration - 1 };
        })
        .filter((condition) => condition.duration !== 0); // Remove expired

      return { ...combatant, conditions: updatedConditions };
    });
    setCombatants(updated);
  };

  const removeCombatant = (id: string) => {
    const filtered = combatants.filter((c) => c.id !== id);
    setCombatants(filtered);

    if (currentTurn >= filtered.length && filtered.length > 0) {
      setCurrentTurn(0);
    }
  };

  const nextTurn = () => {
    if (combatants.length === 0) return;

    const nextIndex = (currentTurn + 1) % combatants.length;

    if (nextIndex === 0) {
      decrementConditionDurations();
      setRound(round + 1);
      toast({
        title: `Round ${round + 1}`,
        description: "A new round has begun! Conditions updated.",
      });
    }

    setCurrentTurn(nextIndex);
  };

  const resetCombat = () => {
    setCombatants([]);
    setCurrentTurn(0);
    setRound(1);
    toast({
      title: "Combat Reset",
      description: "The encounter has been cleared.",
    });
  };

  const sortedCombatants = [...combatants].sort((a, b) => b.initiative - a.initiative);

  if (!character) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <p className="text-muted-foreground">Loading...</p>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background">
      <div className="container max-w-6xl mx-auto p-6">
        <div className="flex items-center justify-between mb-6">
          <Button variant="ghost" onClick={() => navigate(`/character/${id}`)}>
            <ArrowLeft className="w-4 h-4 mr-2" />
            Back to Character
          </Button>
          <div className="flex gap-2">
            <Button variant="outline" onClick={() => navigate("/npc-library")}>
              <Users className="w-4 h-4 mr-2" />
              NPCs
            </Button>
            <Button variant="outline" onClick={() => navigate("/dice")}>
              <Dices className="w-4 h-4 mr-2" />
              Dice
            </Button>
            <Button variant="destructive" onClick={resetCombat}>
              <RotateCcw className="w-4 h-4 mr-2" />
              Reset
            </Button>
          </div>
        </div>

        <Card className="mb-6">
          <CardHeader>
            <div className="flex items-center justify-between">
              <div className="flex items-center gap-3">
                <Swords className="w-6 h-6 text-primary" />
                <div>
                  <CardTitle className="text-2xl">Combat Tracker</CardTitle>
                  <CardDescription>Round {round} тАв {combatants.length} combatants</CardDescription>
                </div>
              </div>
              <div className="flex gap-2">
                {combatants.length > 0 && (
                  <>
                    <Button onClick={rollInitiativeForAll} variant="outline">
                      <RefreshCw className="w-4 h-4 mr-2" />
                      Roll Init
                    </Button>
                    <Button onClick={nextTurn}>
                      Next Turn
                      <ChevronRight className="w-4 h-4 ml-2" />
                    </Button>
                  </>
                )}
              </div>
            </div>
          </CardHeader>
        </Card>

        <div className="grid gap-4 mb-6">
          <Card>
            <CardHeader>
              <CardTitle className="text-lg">Add Combatants</CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="flex gap-2">
                <Button onClick={addPlayerCharacter} variant="outline" className="flex-1">
                  <Plus className="w-4 h-4 mr-2" />
                  Add {character.data.name}
                </Button>
                <Dialog open={showNPCDialog} onOpenChange={setShowNPCDialog}>
                  <DialogTrigger asChild>
                    <Button variant="outline" className="flex-1">
                      <Users className="w-4 h-4 mr-2" />
                      Add from Library
                    </Button>
                  </DialogTrigger>
                  <DialogContent>
                    <DialogHeader>
                      <DialogTitle>Select NPC from Library</DialogTitle>
                      <DialogDescription>
                        Choose an NPC to add to combat
                      </DialogDescription>
                    </DialogHeader>
                    <div className="space-y-2 max-h-[400px] overflow-y-auto">
                      {savedNPCs.length === 0 ? (
                        <p className="text-center text-muted-foreground py-8">
                          No NPCs in library. Create some first!
                        </p>
                      ) : (
                        savedNPCs.map((npc) => (
                          <Card
                            key={npc.id}
                            className="cursor-pointer hover:border-primary transition-colors"
                            onClick={() => addNPCToCombat(npc)}
                          >
                            <CardHeader className="p-4">
                              <div className="flex items-center justify-between">
                                <CardTitle className="text-base">{npc.name}</CardTitle>
                                <div className="text-sm text-muted-foreground">
                                  HP: {npc.hitPoints} | AC: {npc.armorClass}
                                </div>
                              </div>
                            </CardHeader>
                          </Card>
                        ))
                      )}
                    </div>
                  </DialogContent>
                </Dialog>
                <Button onClick={() => setShowAddForm(!showAddForm)} className="flex-1">
                  <Plus className="w-4 h-4 mr-2" />
                  Add Custom
                </Button>
              </div>

              {showAddForm && (
                <div className="grid gap-4 p-4 border rounded-lg bg-muted/50">
                  <div className="grid grid-cols-2 gap-4">
                    <div className="space-y-2">
                      <Label>Name *</Label>
                      <Input
                        placeholder="Goblin"
                        value={newCombatant.name}
                        onChange={(e) => setNewCombatant({ ...newCombatant, name: e.target.value })}
                      />
                    </div>
                    <div className="space-y-2">
                      <Label>Type</Label>
                      <Select
                        value={newCombatant.type}
                        onValueChange={(v) => setNewCombatant({ ...newCombatant, type: v as CombatantType })}
                      >
                        <SelectTrigger>
                          <SelectValue />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="enemy">Enemy</SelectItem>
                          <SelectItem value="ally">Ally</SelectItem>
                        </SelectContent>
                      </Select>
                    </div>
                  </div>

                  <div className="grid grid-cols-3 gap-4">
                    <div className="space-y-2">
                      <Label>Initiative</Label>
                      <Input
                        type="number"
                        placeholder="0"
                        value={newCombatant.initiative}
                        onChange={(e) => setNewCombatant({ ...newCombatant, initiative: e.target.value })}
                      />
                    </div>
                    <div className="space-y-2">
                      <Label>Init Bonus</Label>
                      <Input
                        type="number"
                        placeholder="+2"
                        value={newCombatant.initiativeBonus}
                        onChange={(e) => setNewCombatant({ ...newCombatant, initiativeBonus: e.target.value })}
                      />
                    </div>
                    <div className="space-y-2">
                      <Label>HP *</Label>
                      <Input
                        type="number"
                        placeholder="7"
                        value={newCombatant.hp}
                        onChange={(e) => setNewCombatant({ ...newCombatant, hp: e.target.value })}
                      />
                    </div>
                  </div>

                  <div className="grid grid-cols-2 gap-4">
                    <div className="space-y-2">
                      <Label>AC *</Label>
                      <Input
                        type="number"
                        placeholder="15"
                        value={newCombatant.ac}
                        onChange={(e) => setNewCombatant({ ...newCombatant, ac: e.target.value })}
                      />
                    </div>
                  </div>

                  <div className="flex gap-2">
                    <Button onClick={addCombatant} className="flex-1">
                      Add to Combat
                    </Button>
                    <Button onClick={() => setShowAddForm(false)} variant="outline">
                      Cancel
                    </Button>
                  </div>
                </div>
              )}
            </CardContent>
          </Card>
        </div>

        {combatants.length === 0 ? (
          <Card>
            <CardContent className="py-12 text-center text-muted-foreground">
              <Swords className="w-12 h-12 mx-auto mb-4 opacity-50" />
              <p>No combatants yet. Add your character or enemies to start tracking initiative!</p>
            </CardContent>
          </Card>
        ) : (
          <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
            {sortedCombatants.map((combatant, index) => (
              <CombatParticipant
                key={combatant.id}
                combatant={combatant}
                isCurrentTurn={index === currentTurn}
                onUpdateHP={updateCombatantHP}
                onRemove={removeCombatant}
                onUpdateConditions={updateCombatantConditions}
              />
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { NPC, NPCAbility } from "@/types/npc";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Badge } from "@/components/ui/badge";
import { ArrowLeft, Plus, Trash2, Edit, Users, Swords } from "lucide-react";
import { toast } from "@/hooks/use-toast";

export const NPCLibrary = () => {
  const navigate = useNavigate();
  const [npcs, setNpcs] = useState<NPC[]>([]);
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [editingNPC, setEditingNPC] = useState<NPC | null>(null);
  const [searchQuery, setSearchQuery] = useState("");
  const [filterType, setFilterType] = useState<string>("all");

  const [formData, setFormData] = useState({
    name: "",
    type: "enemy" as "enemy" | "ally" | "neutral",
    hitPoints: "",
    armorClass: "",
    initiativeBonus: "",
    notes: "",
    abilities: [] as NPCAbility[],
  });

  useEffect(() => {
    loadNPCs();
  }, []);

  const loadNPCs = () => {
    const saved = localStorage.getItem("soloquest_npcs");
    if (saved) {
      setNpcs(JSON.parse(saved));
    }
  };

  const saveNPCs = (updatedNPCs: NPC[]) => {
    localStorage.setItem("soloquest_npcs", JSON.stringify(updatedNPCs));
    setNpcs(updatedNPCs);
  };

  const resetForm = () => {
    setFormData({
      name: "",
      type: "enemy",
      hitPoints: "",
      armorClass: "",
      initiativeBonus: "",
      notes: "",
      abilities: [],
    });
    setEditingNPC(null);
  };

  const openEditDialog = (npc: NPC) => {
    setEditingNPC(npc);
    setFormData({
      name: npc.name,
      type: npc.type,
      hitPoints: npc.hitPoints.toString(),
      armorClass: npc.armorClass.toString(),
      initiativeBonus: npc.initiativeBonus?.toString() || "",
      notes: npc.notes || "",
      abilities: npc.abilities || [],
    });
    setIsDialogOpen(true);
  };

  const handleSave = () => {
    if (!formData.name || !formData.hitPoints || !formData.armorClass) {
      toast({
        title: "Missing Information",
        description: "Please fill in name, HP, and AC.",
        variant: "destructive",
      });
      return;
    }

    const npcData: NPC = {
      id: editingNPC?.id || crypto.randomUUID(),
      name: formData.name,
      type: formData.type,
      hitPoints: parseInt(formData.hitPoints),
      armorClass: parseInt(formData.armorClass),
      initiativeBonus: formData.initiativeBonus ? parseInt(formData.initiativeBonus) : undefined,
      abilities: formData.abilities,
      notes: formData.notes,
      createdAt: editingNPC?.createdAt || new Date().toISOString(),
    };

    let updatedNPCs: NPC[];
    if (editingNPC) {
      updatedNPCs = npcs.map((n) => (n.id === editingNPC.id ? npcData : n));
      toast({
        title: "NPC Updated",
        description: `${npcData.name} has been updated.`,
      });
    } else {
      updatedNPCs = [...npcs, npcData];
      toast({
        title: "NPC Added",
        description: `${npcData.name} has been added to your library.`,
      });
    }

    saveNPCs(updatedNPCs);
    setIsDialogOpen(false);
    resetForm();
  };

  const handleDelete = (id: string) => {
    const npc = npcs.find((n) => n.id === id);
    const updatedNPCs = npcs.filter((n) => n.id !== id);
    saveNPCs(updatedNPCs);
    toast({
      title: "NPC Deleted",
      description: `${npc?.name} has been removed from your library.`,
    });
  };

  const addAbility = () => {
    setFormData({
      ...formData,
      abilities: [...formData.abilities, { name: "", description: "" }],
    });
  };

  const updateAbility = (index: number, field: "name" | "description", value: string) => {
    const updated = [...formData.abilities];
    updated[index][field] = value;
    setFormData({ ...formData, abilities: updated });
  };

  const removeAbility = (index: number) => {
    setFormData({
      ...formData,
      abilities: formData.abilities.filter((_, i) => i !== index),
    });
  };

  const filteredNPCs = npcs.filter((npc) => {
    const matchesSearch = npc.name.toLowerCase().includes(searchQuery.toLowerCase());
    const matchesType = filterType === "all" || npc.type === filterType;
    return matchesSearch && matchesType;
  });

  const getTypeBadgeVariant = (type: string) => {
    switch (type) {
      case "enemy":
        return "destructive";
      case "ally":
        return "default";
      default:
        return "secondary";
    }
  };

  return (
    <div className="min-h-screen bg-background">
      <div className="container max-w-6xl mx-auto p-6">
        <div className="flex items-center justify-between mb-6">
          <Button variant="ghost" onClick={() => navigate(-1)}>
            <ArrowLeft className="w-4 h-4 mr-2" />
            Back
          </Button>
        </div>

        <Card className="mb-6">
          <CardHeader>
            <div className="flex items-center justify-between">
              <div className="flex items-center gap-3">
                <Users className="w-6 h-6 text-primary" />
                <div>
                  <CardTitle className="text-2xl">NPC Library</CardTitle>
                  <CardDescription>Manage your NPCs and enemies for quick combat use</CardDescription>
                </div>
              </div>
              <Dialog open={isDialogOpen} onOpenChange={(open) => {
                setIsDialogOpen(open);
                if (!open) resetForm();
              }}>
                <DialogTrigger asChild>
                  <Button>
                    <Plus className="w-4 h-4 mr-2" />
                    Add NPC
                  </Button>
                </DialogTrigger>
                <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto">
                  <DialogHeader>
                    <DialogTitle>{editingNPC ? "Edit NPC" : "Add New NPC"}</DialogTitle>
                    <DialogDescription>
                      Create reusable NPCs and enemies for your encounters
                    </DialogDescription>
                  </DialogHeader>
                  <div className="space-y-4">
                    <div className="grid grid-cols-2 gap-4">
                      <div className="space-y-2">
                        <Label>Name *</Label>
                        <Input
                          placeholder="Goblin Scout"
                          value={formData.name}
                          onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                        />
                      </div>
                      <div className="space-y-2">
                        <Label>Type</Label>
                        <Select
                          value={formData.type}
                          onValueChange={(v: any) => setFormData({ ...formData, type: v })}
                        >
                          <SelectTrigger>
                            <SelectValue />
                          </SelectTrigger>
                          <SelectContent>
                            <SelectItem value="enemy">Enemy</SelectItem>
                            <SelectItem value="ally">Ally</SelectItem>
                            <SelectItem value="neutral">Neutral</SelectItem>
                          </SelectContent>
                        </Select>
                      </div>
                    </div>

                    <div className="grid grid-cols-3 gap-4">
                      <div className="space-y-2">
                        <Label>Hit Points *</Label>
                        <Input
                          type="number"
                          placeholder="7"
                          value={formData.hitPoints}
                          onChange={(e) => setFormData({ ...formData, hitPoints: e.target.value })}
                        />
                      </div>
                      <div className="space-y-2">
                        <Label>Armor Class *</Label>
                        <Input
                          type="number"
                          placeholder="15"
                          value={formData.armorClass}
                          onChange={(e) => setFormData({ ...formData, armorClass: e.target.value })}
                        />
                      </div>
                      <div className="space-y-2">
                        <Label>Initiative Bonus</Label>
                        <Input
                          type="number"
                          placeholder="+2"
                          value={formData.initiativeBonus}
                          onChange={(e) => setFormData({ ...formData, initiativeBonus: e.target.value })}
                        />
                      </div>
                    </div>

                    <div className="space-y-2">
                      <div className="flex items-center justify-between">
                        <Label>Abilities</Label>
                        <Button type="button" variant="outline" size="sm" onClick={addAbility}>
                          <Plus className="w-4 h-4 mr-1" />
                          Add Ability
                        </Button>
                      </div>
                      {formData.abilities.map((ability, index) => (
                        <div key={index} className="p-3 border rounded-lg space-y-2">
                          <div className="flex gap-2">
                            <Input
                              placeholder="Ability name"
                              value={ability.name}
                              onChange={(e) => updateAbility(index, "name", e.target.value)}
                            />
                            <Button
                              type="button"
                              variant="ghost"
                              size="icon"
                              onClick={() => removeAbility(index)}
                            >
                              <Trash2 className="w-4 h-4" />
                            </Button>
                          </div>
                          <Textarea
                            placeholder="Ability description"
                            value={ability.description}
                            onChange={(e) => updateAbility(index, "description", e.target.value)}
                            rows={2}
                          />
                        </div>
                      ))}
                    </div>

                    <div className="space-y-2">
                      <Label>Notes</Label>
                      <Textarea
                        placeholder="Additional notes or lore"
                        value={formData.notes}
                        onChange={(e) => setFormData({ ...formData, notes: e.target.value })}
                        rows={3}
                      />
                    </div>

                    <div className="flex gap-2">
                      <Button onClick={handleSave} className="flex-1">
                        {editingNPC ? "Update NPC" : "Add NPC"}
                      </Button>
                      <Button
                        variant="outline"
                        onClick={() => {
                          setIsDialogOpen(false);
                          resetForm();
                        }}
                      >
                        Cancel
                      </Button>
                    </div>
                  </div>
                </DialogContent>
              </Dialog>
            </div>
          </CardHeader>
        </Card>

        <div className="mb-6 flex gap-4">
          <Input
            placeholder="Search NPCs..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="flex-1"
          />
          <Select value={filterType} onValueChange={setFilterType}>
            <SelectTrigger className="w-[180px]">
              <SelectValue placeholder="Filter by type" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="all">All Types</SelectItem>
              <SelectItem value="enemy">Enemies</SelectItem>
              <SelectItem value="ally">Allies</SelectItem>
              <SelectItem value="neutral">Neutral</SelectItem>
            </SelectContent>
          </Select>
        </div>

        {filteredNPCs.length === 0 ? (
          <Card>
            <CardContent className="py-12 text-center text-muted-foreground">
              <Users className="w-12 h-12 mx-auto mb-4 opacity-50" />
              <p>No NPCs in your library yet. Add some to get started!</p>
            </CardContent>
          </Card>
        ) : (
          <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
            {filteredNPCs.map((npc) => (
              <Card key={npc.id} className="hover:border-primary/50 transition-colors">
                <CardHeader>
                  <div className="flex items-start justify-between">
                    <div className="flex-1">
                      <CardTitle className="text-lg flex items-center gap-2">
                        {npc.name}
                        <Badge variant={getTypeBadgeVariant(npc.type)}>
                          {npc.type}
                        </Badge>
                      </CardTitle>
                      <CardDescription className="mt-2">
                        <div className="flex gap-4 text-sm">
                          <span>HP: {npc.hitPoints}</span>
                          <span>AC: {npc.armorClass}</span>
                          {npc.initiativeBonus !== undefined && (
                            <span>Init: {npc.initiativeBonus >= 0 ? '+' : ''}{npc.initiativeBonus}</span>
                          )}
                        </div>
                      </CardDescription>
                    </div>
                  </div>
                </CardHeader>
                <CardContent className="space-y-4">
                  {npc.abilities && npc.abilities.length > 0 && (
                    <div className="space-y-2">
                      <Label className="text-sm font-semibold">Abilities</Label>
                      {npc.abilities.map((ability, idx) => (
                        <div key={idx} className="text-sm">
                          <span className="font-medium">{ability.name}:</span> {ability.description}
                        </div>
                      ))}
                    </div>
                  )}
                  {npc.notes && (
                    <div className="space-y-1">
                      <Label className="text-sm font-semibold">Notes</Label>
                      <p className="text-sm text-muted-foreground">{npc.notes}</p>
                    </div>
                  )}
                  <div className="flex gap-2 pt-2">
                    <Button variant="outline" size="sm" onClick={() => openEditDialog(npc)} className="flex-1">
                      <Edit className="w-4 h-4 mr-1" />
                      Edit
                    </Button>
                    <Button
                      variant="destructive"
                      size="sm"
                      onClick={() => handleDelete(npc.id)}
                      className="flex-1"
                    >
                      <Trash2 className="w-4 h-4 mr-1" />
                      Delete
                    </Button>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

import { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { JournalEntry, JournalTag, JournalTagType } from "@/types/journal";
import { Character } from "@/types/character";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { JournalEntryCard } from "@/components/JournalEntryCard";
import { ArrowLeft, BookOpen, Plus, Search, X, MapPin, User, Target, Swords, Coins, FileText } from "lucide-react";
import { toast } from "@/hooks/use-toast";

export const SessionJournal = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [character, setCharacter] = useState<Character | null>(null);
  const [entries, setEntries] = useState<JournalEntry[]>([]);
  const [filteredEntries, setFilteredEntries] = useState<JournalEntry[]>([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [filterTag, setFilterTag] = useState<string>("all");
  const [showForm, setShowForm] = useState(false);
  const [editingEntry, setEditingEntry] = useState<JournalEntry | null>(null);

  const [formData, setFormData] = useState({
    title: "",
    content: "",
    sessionNumber: "",
  });
  const [currentTags, setCurrentTags] = useState<JournalTag[]>([]);
  const [newTagType, setNewTagType] = useState<JournalTagType>("general");
  const [newTagValue, setNewTagValue] = useState("");

  useEffect(() => {
    if (id) {
      loadCharacter(id);
      loadEntries(id);
    }
  }, [id]);

  useEffect(() => {
    filterEntries();
  }, [entries, searchQuery, filterTag]);

  const loadCharacter = (characterId: string) => {
    const saved = localStorage.getItem("soloquest_characters");
    if (saved) {
      const characters: Character[] = JSON.parse(saved);
      const found = characters.find((c) => c.id === characterId);
      if (found) {
        setCharacter(found);
      }
    }
  };

  const loadEntries = (characterId: string) => {
    const saved = localStorage.getItem("soloquest_journal");
    if (saved) {
      const allEntries: JournalEntry[] = JSON.parse(saved);
      const characterEntries = allEntries
        .filter((e) => e.characterId === characterId)
        .sort((a, b) => new Date(b.timestamp).getTime() - new Date(a.timestamp).getTime());
      setEntries(characterEntries);
    }
  };

  const saveEntry = () => {
    if (!id || !formData.title.trim() || !formData.content.trim()) {
      toast({
        title: "Missing Information",
        description: "Please provide a title and content for your entry.",
        variant: "destructive",
      });
      return;
    }

    const saved = localStorage.getItem("soloquest_journal");
    const allEntries: JournalEntry[] = saved ? JSON.parse(saved) : [];

    if (editingEntry) {
      // Update existing entry
      const index = allEntries.findIndex((e) => e.id === editingEntry.id);
      if (index !== -1) {
        allEntries[index] = {
          ...editingEntry,
          title: formData.title,
          content: formData.content,
          sessionNumber: formData.sessionNumber ? parseInt(formData.sessionNumber) : undefined,
          tags: currentTags,
        };
      }
    } else {
      // Create new entry
      const newEntry: JournalEntry = {
        id: crypto.randomUUID(),
        characterId: id,
        timestamp: new Date().toISOString(),
        title: formData.title,
        content: formData.content,
        sessionNumber: formData.sessionNumber ? parseInt(formData.sessionNumber) : undefined,
        tags: currentTags,
      };
      allEntries.push(newEntry);
    }

    localStorage.setItem("soloquest_journal", JSON.stringify(allEntries));
    loadEntries(id);
    resetForm();

    toast({
      title: editingEntry ? "Entry Updated" : "Entry Saved",
      description: editingEntry ? "Your journal entry has been updated." : "Your journal entry has been saved.",
    });
  };

  const deleteEntry = (entryId: string) => {
    const saved = localStorage.getItem("soloquest_journal");
    if (saved) {
      const allEntries: JournalEntry[] = JSON.parse(saved);
      const filtered = allEntries.filter((e) => e.id !== entryId);
      localStorage.setItem("soloquest_journal", JSON.stringify(filtered));
      loadEntries(id!);

      toast({
        title: "Entry Deleted",
        description: "Your journal entry has been deleted.",
      });
    }
  };

  const editEntry = (entry: JournalEntry) => {
    setEditingEntry(entry);
    setFormData({
      title: entry.title,
      content: entry.content,
      sessionNumber: entry.sessionNumber?.toString() || "",
    });
    setCurrentTags(entry.tags);
    setShowForm(true);
  };

  const resetForm = () => {
    setFormData({ title: "", content: "", sessionNumber: "" });
    setCurrentTags([]);
    setNewTagValue("");
    setShowForm(false);
    setEditingEntry(null);
  };

  const addTag = () => {
    if (newTagValue.trim()) {
      setCurrentTags([...currentTags, { type: newTagType, value: newTagValue.trim() }]);
      setNewTagValue("");
    }
  };

  const removeTag = (index: number) => {
    setCurrentTags(currentTags.filter((_, i) => i !== index));
  };

  const filterEntries = () => {
    let filtered = [...entries];

    if (searchQuery) {
      const query = searchQuery.toLowerCase();
      filtered = filtered.filter(
        (e) =>
          e.title.toLowerCase().includes(query) ||
          e.content.toLowerCase().includes(query) ||
          e.tags.some((t) => t.value.toLowerCase().includes(query))
      );
    }

    if (filterTag !== "all") {
      filtered = filtered.filter((e) => e.tags.some((t) => t.type === filterTag));
    }

    setFilteredEntries(filtered);
  };

  const tagTypeIcons: Record<JournalTagType, any> = {
    location: MapPin,
    npc: User,
    quest: Target,
    combat: Swords,
    loot: Coins,
    general: FileText,
  };

  if (!character) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <p className="text-muted-foreground">Loading...</p>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background">
      <div className="container max-w-5xl mx-auto p-6">
        <div className="flex items-center justify-between mb-6">
          <Button variant="ghost" onClick={() => navigate(`/character/${id}`)}>
            <ArrowLeft className="w-4 h-4 mr-2" />
            Back to Character
          </Button>
          <Button onClick={() => setShowForm(!showForm)}>
            {showForm ? <X className="w-4 h-4 mr-2" /> : <Plus className="w-4 h-4 mr-2" />}
            {showForm ? "Cancel" : "New Entry"}
          </Button>
        </div>

        <Card className="mb-6">
          <CardHeader>
            <div className="flex items-center gap-3">
              <BookOpen className="w-6 h-6 text-primary" />
              <div>
                <CardTitle className="text-2xl">Session Journal</CardTitle>
                <CardDescription>
                  {character.data.name}'s Adventure Log
                </CardDescription>
              </div>
            </div>
          </CardHeader>
        </Card>

        {showForm && (
          <Card className="mb-6 border-primary/50">
            <CardHeader>
              <CardTitle>{editingEntry ? "Edit Entry" : "New Journal Entry"}</CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2 col-span-2 sm:col-span-1">
                  <label className="text-sm font-medium">Title</label>
                  <Input
                    placeholder="Entry title..."
                    value={formData.title}
                    onChange={(e) => setFormData({ ...formData, title: e.target.value })}
                  />
                </div>
                <div className="space-y-2 col-span-2 sm:col-span-1">
                  <label className="text-sm font-medium">Session # (optional)</label>
                  <Input
                    type="number"
                    placeholder="1"
                    value={formData.sessionNumber}
                    onChange={(e) => setFormData({ ...formData, sessionNumber: e.target.value })}
                  />
                </div>
              </div>

              <div className="space-y-2">
                <label className="text-sm font-medium">Content</label>
                <Textarea
                  placeholder="What happened in this session..."
                  value={formData.content}
                  onChange={(e) => setFormData({ ...formData, content: e.target.value })}
                  rows={6}
                />
              </div>

              <div className="space-y-2">
                <label className="text-sm font-medium">Tags</label>
                <div className="flex gap-2">
                  <Select value={newTagType} onValueChange={(v) => setNewTagType(v as JournalTagType)}>
                    <SelectTrigger className="w-32">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="location">Location</SelectItem>
                      <SelectItem value="npc">NPC</SelectItem>
                      <SelectItem value="quest">Quest</SelectItem>
                      <SelectItem value="combat">Combat</SelectItem>
                      <SelectItem value="loot">Loot</SelectItem>
                      <SelectItem value="general">General</SelectItem>
                    </SelectContent>
                  </Select>
                  <Input
                    placeholder="Tag value..."
                    value={newTagValue}
                    onChange={(e) => setNewTagValue(e.target.value)}
                    onKeyPress={(e) => e.key === "Enter" && addTag()}
                    className="flex-1"
                  />
                  <Button onClick={addTag} type="button">
                    <Plus className="w-4 h-4" />
                  </Button>
                </div>

                {currentTags.length > 0 && (
                  <div className="flex flex-wrap gap-2 mt-2">
                    {currentTags.map((tag, idx) => {
                      const TagIcon = tagTypeIcons[tag.type];
                      return (
                        <Badge key={idx} variant="secondary" className="gap-1">
                          <TagIcon className="w-3 h-3" />
                          {tag.value}
                          <button onClick={() => removeTag(idx)} className="ml-1">
                            <X className="w-3 h-3" />
                          </button>
                        </Badge>
                      );
                    })}
                  </div>
                )}
              </div>

              <div className="flex gap-2 pt-4">
                <Button onClick={saveEntry} className="flex-1">
                  {editingEntry ? "Update Entry" : "Save Entry"}
                </Button>
                <Button variant="outline" onClick={resetForm}>
                  Cancel
                </Button>
              </div>
            </CardContent>
          </Card>
        )}

        <div className="flex gap-4 mb-6">
          <div className="flex-1 relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-muted-foreground" />
            <Input
              placeholder="Search entries..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="pl-10"
            />
          </div>
          <Select value={filterTag} onValueChange={setFilterTag}>
            <SelectTrigger className="w-40">
              <SelectValue placeholder="Filter by tag" />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="all">All Tags</SelectItem>
              <SelectItem value="location">Location</SelectItem>
              <SelectItem value="npc">NPC</SelectItem>
              <SelectItem value="quest">Quest</SelectItem>
              <SelectItem value="combat">Combat</SelectItem>
              <SelectItem value="loot">Loot</SelectItem>
              <SelectItem value="general">General</SelectItem>
            </SelectContent>
          </Select>
        </div>

        <div className="space-y-4">
          {filteredEntries.length === 0 ? (
            <Card>
              <CardContent className="py-12 text-center text-muted-foreground">
                {entries.length === 0 ? (
                  <div>
                    <BookOpen className="w-12 h-12 mx-auto mb-4 opacity-50" />
                    <p>No journal entries yet. Start documenting your adventure!</p>
                  </div>
                ) : (
                  <p>No entries match your search.</p>
                )}
              </CardContent>
            </Card>
          ) : (
            filteredEntries.map((entry) => (
              <JournalEntryCard
                key={entry.id}
                entry={entry}
                onEdit={editEntry}
                onDelete={deleteEntry}
              />
            ))
          )}
        </div>
      </div>
    </div>
  );
};

import type { Config } from "tailwindcss";

export default {
  darkMode: ["class"],
  content: ["./pages/**/*.{ts,tsx}", "./components/**/*.{ts,tsx}", "./app/**/*.{ts,tsx}", "./src/**/*.{ts,tsx}"],
  prefix: "",
  theme: {
    container: {
      center: true,
      padding: "2rem",
      screens: {
        "2xl": "1400px",
      },
    },
    extend: {
      colors: {
        border: "hsl(var(--border))",
        input: "hsl(var(--input))",
        ring: "hsl(var(--ring))",
        background: "hsl(var(--background))",
